INSERT INTO `categories` (`id`, `name`, `image`, `description`, `status`) VALUES
(1, 'Giày Cỏ Nhân Tạo', 'category-1.jpg', 'Là dòng giày đá bóng được thiết kế đặc biệt cho mặt sân cỏ nhân tạo từ 5 đến 7 người với bộ đế TF bám sân chắc chắn, hỗ trợ tối đa khả năng kiểm soát bóng và di chuyển linh hoạt. ', 1),
(2, 'Giày Futsal', 'category-2.webp', 'Là dòng giày đá bóng được thiết kế đặc biệt dành cho mặt sân sàn gỗ hoặc bê tông, với bề mặt đế IC (Non-Marking) bằng phẳng cùng các rãnh nhỏ giúp tăng độ bám, hỗ trợ tối đa khả năng kiểm soát bóng bằng gầm giày, xoay trở linh hoạt và thực hiện các động tác kỹ thuật.', 1),
(3, 'Phụ kiện', 'category-3.webp', 'Thanh Hùng Futsal tự hào là đại lý phân phối chính hãng các sản phẩm phụ kiện đến từ các thương hiệu nổi tiếng như Activital - vớ thể thao, Grandsport - phụ kiện thể thao, StarBalm - chai xịt nóng, lạnh, Bóng Động Lực - trái banh thi đấu chính thức tại các giải đấu lớn.', 1);
